import React, { useState, useEffect } from "react";
import jsPDF from "jspdf";
import autoTable from "jspdf-autotable";
import "./ServerTable.css";

const ServerTable = () => {
  // Load data from localStorage to persist after refresh
  const [data, setData] = useState(() => {
    const savedData = localStorage.getItem("serverData");
    return savedData ? JSON.parse(savedData) : [
      {
        serviceName: "Leads (tahmida)",
        production: [
          { purpose: "Database", cpu: "1", memory: "2", hdd: "2", status: "2" },
          { purpose: "Application", cpu: "", memory: "", hdd: "", status: "" },
          { purpose: "Others", cpu: "", memory: "", hdd: "", status: "" },
        ],
        devUAT: [
          { purpose: "Database", cpu: 4, memory: 8, hdd: 150, status: "1" },
          { purpose: "Application", cpu: 4, memory: 8, hdd: 150, status: "2" },
          { purpose: "Other (FS)", cpu: 4, memory: 8, hdd: 150, status: "3" },
        ],
      },
    ];
  });

  // Save data to localStorage whenever it changes
  useEffect(() => {
    localStorage.setItem("serverData", JSON.stringify(data));
  }, [data]);

  // Function to add a new service row
  const addRow = () => {
    console.log("Add Service Clicked!");

    const newRow = {
      serviceName: "New Service",
      production: [
        { purpose: "Database", cpu: 4, memory: 8, hdd: 150, status: "Active" },
        { purpose: "Application", cpu: "", memory: "", hdd: "", status: "" },
        { purpose: "Others", cpu: "", memory: "", hdd: "", status: "" },
      ],
      devUAT: [
        { purpose: "Database", cpu: 4, memory: 8, hdd: 150, status: "Active" },
        { purpose: "Application", cpu: 4, memory: 8, hdd: 150, status: "Pending" },
        { purpose: "Other (FS)", cpu: 4, memory: 8, hdd: 150, status: "Completed" },
      ],
    };

    setData((prevData) => [...prevData, newRow]);
  };

  // Function to handle editing cells
  const handleEdit = (serviceIndex, category, rowIndex, field, value) => {
    const newData = [...data];
    newData[serviceIndex][category][rowIndex][field] = value;
    setData(newData);
  };

  // Function to download the table as a PDF
  const downloadPDF = () => {
    const doc = new jsPDF();
    const table = document.getElementById("table-to-download");

    if (!table) {
      console.error("Table not found!");
      return;
    }

    let data = [];
    let headers = [];

    // Extract headers
    const headerCells = table.querySelectorAll("thead tr th");
    headers = Array.from(headerCells).map((header) => header.innerText.trim());

    // Extract table rows
    const rows = table.querySelectorAll("tbody tr");
    rows.forEach((row) => {
      let rowData = [];
      const cells = row.querySelectorAll("td input");
      cells.forEach((cell) => {
        rowData.push(cell.value.trim());
      });
      data.push(rowData);
    });

    // Generate PDF table correctly
    autoTable(doc, {
      head: [headers],
      body: data,
      startY: 20,
      theme: "grid",
      styles: {
        fontSize: 10,
        cellPadding: 3,
      },
      margin: { top: 15 },
    });

    doc.save("server-table.pdf");
  };

  const printTable = () => {
    window.print();
  };

  return (
    <div className="container">
      <table id="table-to-download" className="server-table">
        <thead>
          <tr>
            <th rowSpan="2">Sl.</th>
            <th rowSpan="2">Service Name</th>
            <th colSpan="5">Production Server</th>
            <th colSpan="5">Dev/UAT Server</th>
          </tr>
          <tr>
            <th>Purpose</th>
            <th>CPU (Core)</th>
            <th>Memory (GB)</th>
            <th>HDD (GB)</th>
            <th>Status</th>
            <th>Purpose</th>
            <th>CPU (Core)</th>
            <th>Memory (GB)</th>
            <th>HDD (GB)</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {data.map((service, index) =>
            service.production.map((prod, prodIndex) => (
              <tr key={`${index}-${prodIndex}`}>
                {prodIndex === 0 && (
                  <td rowSpan={service.production.length}>{index + 1}</td>
                )}
                {prodIndex === 0 && (
                  <td rowSpan={service.production.length}>
                    <input
                      type="text"
                      value={service.serviceName}
                      onChange={(e) =>
                        handleEdit(index, "serviceName", 0, "serviceName", e.target.value)
                      }
                    />
                  </td>
                )}
                <td>
                  <input
                    type="text"
                    value={prod.purpose}
                    onChange={(e) =>
                      handleEdit(index, "production", prodIndex, "purpose", e.target.value)
                    }
                  />
                </td>
                <td>
                  <input
                    type="number"
                    value={prod.cpu}
                    onChange={(e) =>
                      handleEdit(index, "production", prodIndex, "cpu", e.target.value)
                    }
                  />
                </td>
                <td>
                  <input
                    type="number"
                    value={prod.memory}
                    onChange={(e) =>
                      handleEdit(index, "production", prodIndex, "memory", e.target.value)
                    }
                  />
                </td>
                <td>
                  <input
                    type="number"
                    value={prod.hdd}
                    onChange={(e) =>
                      handleEdit(index, "production", prodIndex, "hdd", e.target.value)
                    }
                  />
                </td>
                <td>
                  <input
                    type="text"
                    value={prod.status}
                    onChange={(e) =>
                      handleEdit(index, "production", prodIndex, "status", e.target.value)
                    }
                  />
                </td>

                {/* Dev/UAT Server */}
                <td>
                  <input type="text" value={service.devUAT[prodIndex].purpose} disabled />
                </td>
                <td>
                  <input
                    type="number"
                    value={service.devUAT[prodIndex].cpu}
                    onChange={(e) =>
                      handleEdit(index, "devUAT", prodIndex, "cpu", e.target.value)
                    }
                  />
                </td>
                <td>
                  <input
                    type="number"
                    value={service.devUAT[prodIndex].memory}
                    onChange={(e) =>
                      handleEdit(index, "devUAT", prodIndex, "memory", e.target.value)
                    }
                  />
                </td>
                <td>
                  <input
                    type="number"
                    value={service.devUAT[prodIndex].hdd}
                    onChange={(e) =>
                      handleEdit(index, "devUAT", prodIndex, "hdd", e.target.value)
                    }
                  />
                </td>
                <td>
                  <input type="text" value={service.devUAT[prodIndex].status} disabled />
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>
      <button className="add-btn" onClick={addRow}>âž• Add Service</button>
      <button onClick={printTable}>ðŸ–¨ Print Table</button>
    </div>
  );
};

export default ServerTable;
