import React, { useState, useEffect } from "react";
import axios from "axios";

const ServerTable = () => {
  const [data, setData] = useState([]);
  const [editIndex, setEditIndex] = useState(null); // Tracks which row is being edited

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = () => {
    axios.get("http://localhost:5000/api/servers")
      .then((response) => setData(response.data))
      .catch((error) => console.error("Error fetching servers:", error));
  };

  const handleEdit = (serviceIndex, rowIndex, type, field, value) => {
    const newData = [...data];
    newData[serviceIndex][type][rowIndex][field] = value;
    setData(newData);
  };

  const handleSave = () => {
    axios.post("http://localhost:5000/api/update", data)
      .then(() => {
        setEditIndex(null);
        fetchData();
      })
      .catch((error) => console.error("Error saving data:", error));
  };

  const handleAddRow = () => {
    const newService = {
      serviceName: "New Service",
      production: [
        { purpose: "Database", cpu: "", memory: "", hdd: "", status: "" },
        { purpose: "Application", cpu: "", memory: "", hdd: "", status: "" },
        { purpose: "Others", cpu: "", memory: "", hdd: "", status: "" },
      ],
      devUAT: [
        { purpose: "Database", cpu: "", memory: "", hdd: "", status: "" },
        { purpose: "Application", cpu: "", memory: "", hdd: "", status: "" },
        { purpose: "Other (FS)", cpu: "", memory: "", hdd: "", status: "" },
      ],
    };
    setData([...data, newService]);
  };

  return (
    <div>
      <table border="1">
        <thead>
          <tr>
            <th rowSpan="2">Sl.</th>
            <th rowSpan="2">Service Name</th>
            <th colSpan="5">Production Server</th>
            <th colSpan="5">Dev/UAT Server</th>
          </tr>
          <tr>
            <th>Purpose</th>
            <th>CPU (Core)</th>
            <th>Memory (GB)</th>
            <th>HDD (GB)</th>
            <th>Status</th>
            <th>Purpose</th>
            <th>CPU (Core)</th>
            <th>Memory (GB)</th>
            <th>HDD (GB)</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {data.map((service, serviceIndex) =>
            service.production.map((prod, prodIndex) => (
              <tr key={`${serviceIndex}-${prodIndex}`}>
                {prodIndex === 0 && (
                  <>
                    <td rowSpan={service.production.length}>{serviceIndex + 1}</td>
                    <td rowSpan={service.production.length}>{service.serviceName}</td>
                  </>
                )}
                <td>{prod.purpose}</td>
                <td>
                  {editIndex === serviceIndex ? (
                    <input
                      type="text"
                      value={prod.cpu}
                      onChange={(e) => handleEdit(serviceIndex, prodIndex, "production", "cpu", e.target.value)}
                    />
                  ) : (
                    prod.cpu
                  )}
                </td>
                <td>
                  {editIndex === serviceIndex ? (
                    <input
                      type="text"
                      value={prod.memory}
                      onChange={(e) => handleEdit(serviceIndex, prodIndex, "production", "memory", e.target.value)}
                    />
                  ) : (
                    prod.memory
                  )}
                </td>
                <td>
                  {editIndex === serviceIndex ? (
                    <input
                      type="text"
                      value={prod.hdd}
                      onChange={(e) => handleEdit(serviceIndex, prodIndex, "production", "hdd", e.target.value)}
                    />
                  ) : (
                    prod.hdd
                  )}
                </td>
                <td>
                  {editIndex === serviceIndex ? (
                    <input
                      type="text"
                      value={prod.status}
                      onChange={(e) => handleEdit(serviceIndex, prodIndex, "production", "status", e.target.value)}
                    />
                  ) : (
                    prod.status
                  )}
                </td>
                <td>{service.devUAT[prodIndex].purpose}</td>
                <td>
                  {editIndex === serviceIndex ? (
                    <input
                      type="text"
                      value={service.devUAT[prodIndex].cpu}
                      onChange={(e) => handleEdit(serviceIndex, prodIndex, "devUAT", "cpu", e.target.value)}
                    />
                  ) : (
                    service.devUAT[prodIndex].cpu
                  )}
                </td>
                <td>
                  {editIndex === serviceIndex ? (
                    <input
                      type="text"
                      value={service.devUAT[prodIndex].memory}
                      onChange={(e) => handleEdit(serviceIndex, prodIndex, "devUAT", "memory", e.target.value)}
                    />
                  ) : (
                    service.devUAT[prodIndex].memory
                  )}
                </td>
                <td>
                  {editIndex === serviceIndex ? (
                    <input
                      type="text"
                      value={service.devUAT[prodIndex].hdd}
                      onChange={(e) => handleEdit(serviceIndex, prodIndex, "devUAT", "hdd", e.target.value)}
                    />
                  ) : (
                    service.devUAT[prodIndex].hdd
                  )}
                </td>
                <td>
                  {editIndex === serviceIndex ? (
                    <input
                      type="text"
                      value={service.devUAT[prodIndex].status}
                      onChange={(e) => handleEdit(serviceIndex, prodIndex, "devUAT", "status", e.target.value)}
                    />
                  ) : (
                    service.devUAT[prodIndex].status
                  )}
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>
      <button onClick={handleAddRow}>‚ûï Add Row</button>
      {editIndex === null ? (
        <button onClick={() => setEditIndex(0)}>‚úèÔ∏è Edit</button>
      ) : (
        <button onClick={handleSave}>üíæ Save</button>
      )}
    </div>
  );
};

export default ServerTable;
