import React, { useState, useEffect } from "react";
import axios from "axios";

const ServerTable = () => {
  const [data, setData] = useState([]);
  const [editIndex, setEditIndex] = useState(null);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = () => {
    axios.get("http://localhost:5000/api/servers")
      .then((response) => setData(response.data))
      .catch((error) => console.error("Error fetching servers:", error));
  };

  const handleEdit = (serviceIndex, rowIndex, type, field, value) => {
    const newData = [...data];
    newData[serviceIndex][type][rowIndex][field] = value;
    setData(newData);
  };

  const handleSave = () => {
    axios.post("http://localhost:5000/api/update", data)
      .then(() => {
        setEditIndex(null);
        fetchData();
      })
      .catch((error) => console.error("Error saving data:", error));
  };

  const handleAddRow = () => {
    axios.post("http://localhost:5000/api/addService", { serviceName: "New Service" })
      .then(() => fetchData())
      .catch((error) => console.error("Error adding service:", error));
  };

  return (
    <div>
      <table border="1">
        <thead>
          <tr>
            <th>Sl.</th>
            <th>Service Name</th>
            <th>Production Server</th>
            <th>Dev/UAT Server</th>
          </tr>
        </thead>
        <tbody>
          {data.map((service, index) => (
            <tr key={index}>
              <td>{index + 1}</td>
              <td>{service.serviceName}</td>
              <td>
                {service.production.map((row, rowIndex) => (
                  <div key={rowIndex}>
                    <input type="text" value={row.cpu} onChange={(e) => handleEdit(index, rowIndex, "production", "cpu", e.target.value)} />
                  </div>
                ))}
              </td>
              <td>
                {service.devUAT.map((row, rowIndex) => (
                  <div key={rowIndex}>
                    <input type="text" value={row.cpu} onChange={(e) => handleEdit(index, rowIndex, "devUAT", "cpu", e.target.value)} />
                  </div>
                ))}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <button onClick={handleAddRow}>‚ûï Add Service</button>
      {editIndex === null ? (
        <button onClick={() => setEditIndex(0)}>‚úèÔ∏è Edit</button>
      ) : (
        <button onClick={handleSave}>üíæ Save</button>
      )}
    </div>
  );
};

export default ServerTable;
